import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import SignUpPage from './Components/SignUpPage';
import Home from './Components/Home';
import EventsList from './Components/EventsList';
import NominationForm from './Components/NominationForm';
import VotingPage from './Components/VotingPage';
import ResultPage from './Components/ResultPage';
const nominationStartDate = new Date('2024-08-01'); // Replace with your actual dates
const nominationEndDate = new Date('2024-08-15');
const electionStartDate = new Date('2024-08-16');
const electionEndDate = new Date('2024-08-25');
const resultsDate = new Date('2024-08-26');

const getCurrentPeriod = () => {
  const today = new Date();

  if (today >= nominationStartDate && today <= nominationEndDate) {
    return 'nomination';
  } else if (today >= electionStartDate && today <= electionEndDate) {
    return 'election';
  } else if (today >= resultsDate) {
    return 'results';
  } else {
    return 'inactive';
  }
};

const App = () => {
  const currentPeriod = getCurrentPeriod();

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<SignUpPage />} />
          <Route path="/home" element={<Home userType="student" />} />
          <Route path="/admin/home" element={<Home userType="admin" />} />

          {/* Route based on the current period */}
          <Route
            path="/events"
            element={
              currentPeriod === 'nomination' ? (
                <EventsList />
              ) : currentPeriod === 'election' ? (
                <VotingPage />
              ) : currentPeriod === 'results' ? (
                <ResultPage />
              ) : (
                <div>Events are currently inactive.</div>
              )
            }
          />
          <Route path="/nomination" element={<NominationForm />} />
          <Route path="/voting" element={<VotingPage />} />
          <Route path="/results" element={<ResultPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
