import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Form, Button } from 'react-bootstrap';

const NominationForm = () => {
  const { eventId } = useParams(); // Get eventId from URL
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    cgpa: '',
    attendance: '',
    role: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send formData to backend here
    console.log('Form Submitted:', formData);
    alert('Your nomination has been submitted!');
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Nominate Yourself for Event {eventId}</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" name="name" value={formData.name} onChange={handleChange} required />
        </Form.Group>

        <Form.Group controlId="formEmail" className="mt-3">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} required />
        </Form.Group>

        <Form.Group controlId="formPhone" className="mt-3">
          <Form.Label>Phone</Form.Label>
          <Form.Control type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
        </Form.Group>

        <Form.Group controlId="formCgpa" className="mt-3">
          <Form.Label>CGPA</Form.Label>
          <Form.Control type="number" name="cgpa" value={formData.cgpa} onChange={handleChange} required />
        </Form.Group>

        <Form.Group controlId="formAttendance" className="mt-3">
          <Form.Label>Attendance (%)</Form.Label>
          <Form.Control type="number" name="attendance" value={formData.attendance} onChange={handleChange} required />
        </Form.Group>

        <Form.Group controlId="formRole" className="mt-3">
          <Form.Label>Role</Form.Label>
          <Form.Control as="select" name="role" value={formData.role} onChange={handleChange} required>
            <option value="">Select a role</option>
            <option value="President">President</option>
            <option value="Vice President">Vice President</option>
            <option value="Secretary">Secretary</option>
            <option value="Treasurer">Treasurer</option>
            {/* Add more roles as needed */}
          </Form.Control>
        </Form.Group>

        <Button variant="primary" type="submit" className="mt-4">Submit Nomination</Button>
      </Form>
    </Container>
  );
};

export default NominationForm;
